api.query:
query characterByName($name: String!) {
  characters(filter: {name: $name}) {
    results {
      name
      species
      status
      type
      gender
      origin {
        name
        type
        dimension
      }
      location {
        name
        type
        dimension
      }
      episode {
        name
        air_date
        episode
      }
      created
    }
  }
}

query episodeById($episodeid: String!) {
  episodes(filter: {episode: $episodeid}) {
    results {
      name
      air_date
      characters {
        name
        species
        type
        gender
      }
    }
  }
}
