api.query:
query SensorReadings($sensorid:Int!, $limit: Int = 60) {
  SecReading(sensorid: $sensorid, limit: $limit) {
    timeSec
    temp
  }
}

query MaxTemp($sensorid:Int, $limit: Int = 60) {
  SensorMaxTemp(sensorid: $sensorid, limit: $limit) {
    sensorid
    maxTemp
  }
}

query MaxTempLastMin($sensorid:Int, $limit: Int = 60) {
  SensorMaxTempLastMinute(sensorid: $sensorid, limit: $limit) {
    sensorid
    maxTemp
  }
}
